mapper.select:
select 
proj.*,comp.company_name
from t_project proj left join t_company comp on comp.id = proj.company_id and comp.status = 1
where
  proj.status = 1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and project_name like concat('%',concat(#{projectName,jdbcType=VARCHAR},'%'))


    and comp.company_name like concat('%',concat(#{companyName,jdbcType=VARCHAR},'%'))


    and proj.id = #{id,jdbcType=VARCHAR}


    and proj.is_completion = #{completion,jdbcType=TINYINT}

order by proj.create_time desc

  limit #{pageStart,jdbcType=INTEGER},#{pageEnd,jdbcType=INTEGER}

select count(1) from (
select
proj.*,comp.company_name
from t_project proj left join t_company comp on comp.id = proj.company_id and comp.status = 1
where proj.status = 1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and project_name like concat('%',concat(#{projectName,jdbcType=VARCHAR},'%'))


    and comp.company_name like concat('%',concat(#{companyName,jdbcType=VARCHAR},'%'))


    and proj.id = #{id,jdbcType=VARCHAR}


    and proj.is_completion = #{completion,jdbcType=TINYINT}

)aa

mapper.update:
update t_project
company_id = #{companyId,jdbcType=VARCHAR}, project_name = #{projectName,jdbcType=VARCHAR}, project_code = #{projectCode,jdbcType=VARCHAR}, year = #{year,jdbcType=VARCHAR}, supply_unit = #{supplyUnit,jdbcType=VARCHAR}, contract_sign_time = #{contractSignTime,jdbcType=VARCHAR}, supply_time = #{supplyTime,jdbcType=VARCHAR}, contract_num = #{contractNum,jdbcType=VARCHAR}, contract_amount = #{contractAmount,jdbcType=VARCHAR}, settlement_mode = #{settlementMode,jdbcType=VARCHAR}, base_float_value = #{baseFloatValue,jdbcType=VARCHAR}, extra_capital_amount = #{extraCapitalAmount,jdbcType=VARCHAR}, capital_time_limit = #{capitalTimeLimit,jdbcType=VARCHAR}, interest_rate = #{interestRate,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, create_by = #{createBy,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_by = #{updateBy,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, project_assistant = #{projectAssistant,jdbcType=VARCHAR}, operate_assistant = #{operateAssistant,jdbcType=VARCHAR}, settlement_assistant = #{settlementAssistant,jdbcType=VARCHAR}, finance_assistant = #{financeAssistant,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
