
DROP TABLE IF EXISTS `t_company`;

CREATE TABLE `t_company` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键ID UUID',
  `company_name` varchar(60) COLLATE utf8_bin DEFAULT NULL COMMENT '公司名称',
  `company_short_name` varchar(60) COLLATE utf8_bin DEFAULT NULL COMMENT '公司简称',
  `parent_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '公司上级ID',
  `sort` int(11) DEFAULT NULL COMMENT '排序',
  `level` int(11) DEFAULT NULL COMMENT '菜单层级',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='公司信息';



DROP TABLE IF EXISTS `t_credit`;

CREATE TABLE `t_credit` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键ID UUID',
  `company_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '项目编码',
  `project_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '项目ID',
  `credit_code` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '信用证编号',
  `credit_type` int(11) DEFAULT '1' COMMENT '信用证类型 1公司大证 2项目小证',
  `open_amount` double DEFAULT '0' COMMENT '开证金额',
  `open_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '开证日期',
  `rest_amount` double DEFAULT '0' COMMENT '信用证余额',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='项目信用证信息';




DROP TABLE IF EXISTS `t_order`;

CREATE TABLE `t_order` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键ID',
  `project_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '项目ID',
  `order_code` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '订单编号',
  `order_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '开证日期',
  `order_status` int(11) DEFAULT NULL COMMENT '订单状态',
  `order_batch_no` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '订单批次号',
  `delivery_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '交验时间',
  `delivery_status` int(11) DEFAULT NULL COMMENT '交验状态',
  `receive_num` varchar(30) COLLATE utf8_bin DEFAULT '0.0' COMMENT '验收数量',
  `quality` int(11) DEFAULT NULL COMMENT '质量',
  `quality_remark` varchar(300) COLLATE utf8_bin DEFAULT NULL COMMENT '质量备注',
  `service` int(11) DEFAULT NULL COMMENT '服务',
  `service_remark` varchar(300) COLLATE utf8_bin DEFAULT NULL COMMENT '服务备注',
  `settlement_mode` int(11) DEFAULT NULL COMMENT '结算方式',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `order_exception_desc` varchar(1000) COLLATE utf8_bin DEFAULT NULL,
  `delivery_exception_desc` varchar(1000) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='订单信息表';





DROP TABLE IF EXISTS `t_payment`;

CREATE TABLE `t_payment` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键ID uuid',
  `settlement_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '结算单ID',
  `bill_open_time` date DEFAULT NULL COMMENT '需方发票开立时间',
  `bill_open_status` int(11) DEFAULT NULL COMMENT '需方发票开立状态 1-正常 2-异常',
  `bill_open_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '需方发票开立备注',
  `payment_code` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '支付编号',
  `approval_time` date DEFAULT NULL COMMENT '审批完成时间',
  `approval_status` int(11) DEFAULT NULL COMMENT '审批完成状态 1-正常 2-异常',
  `approval_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '审批完成备注',
  `payment_time` date DEFAULT NULL COMMENT '付款时间',
  `payment_status` int(11) DEFAULT NULL COMMENT '付款状态 1-正常 2-异常',
  `payment_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '付款备注',
  `payment_amount` double DEFAULT NULL COMMENT '付款金额',
  `credit_surplus_amount` double DEFAULT NULL COMMENT '余额',
  `payment_source_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '支付源ID 根据结算模式确定, 信用证为对应的信用证ID, 代购例外为对应的收款ID',
  `remarks` varchar(500) COLLATE utf8_bin DEFAULT NULL COMMENT '备注',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='支付信息表';




DROP TABLE IF EXISTS `t_project`;

CREATE TABLE `t_project` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键UUID',
  `company_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '公司ID',
  `project_name` varchar(60) COLLATE utf8_bin DEFAULT NULL COMMENT '项目名称',
  `project_code` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '项目编号',
  `year` varchar(10) COLLATE utf8_bin DEFAULT NULL COMMENT '年份',
  `supply_unit` varchar(60) COLLATE utf8_bin DEFAULT NULL COMMENT '供应单位',
  `contract_sign_time` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '合同签订时间',
  `supply_time` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '供货时间段',
  `contract_num` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '合同数量',
  `contract_amount` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '合同金额',
  `settlement_mode` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '结算方式 1-信用证 2-代购 3-信用证例外',
  `base_float_value` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '基价浮动值',
  `extra_capital_amount` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '例外垫资额度',
  `capital_time_limit` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '垫资期限',
  `interest_rate` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '利率标准',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='项目信息表';




DROP TABLE IF EXISTS `t_receipt`;

CREATE TABLE `t_receipt` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键ID UUID',
  `company_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '项目编码',
  `project_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '项目ID',
  `receipt_amount` double DEFAULT '0' COMMENT '收款金额',
  `receipt_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '收款日期',
  `remarks` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '备注',
  `receipt_balance` double DEFAULT NULL COMMENT '余额',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='收款信息表';



DROP TABLE IF EXISTS `t_settlement`;

CREATE TABLE `t_settlement` (
  `id` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '主键ID uuid',
  `order_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '订单号',
  `company_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '公司ID',
  `project_id` varchar(32) COLLATE utf8_bin DEFAULT NULL COMMENT '项目ID',
  `settlement_code` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '结算单号',
  `supplier_settlement_time` date DEFAULT NULL COMMENT '供方结算时间',
  `supplier_settlement_status` int(11) DEFAULT NULL COMMENT '供方结算状态  1-正常 2-异常',
  `supplier_settlement_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '供方结算备注',
  `settlement_delivery_time` date DEFAULT NULL COMMENT '供方结算单据送达时间',
  `settlement_delivery_status` int(11) DEFAULT NULL COMMENT '供方结算单据送达状态  1-正常 2-异常',
  `settlement_delivery_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '供方结算单据送达备注',
  `bill_delivery_time` date DEFAULT NULL COMMENT '供方发票送达时间',
  `bill_delivery_status` int(11) DEFAULT NULL COMMENT '供方发票送达状态  1-正常 2-异常',
  `bill_delivery_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '供方发票送达备注',
  `buyer_settlement_time` date DEFAULT NULL COMMENT '需方结算时间',
  `buyer_settlement_status` int(11) DEFAULT NULL COMMENT '需方结算状态 1-正常 2-异常',
  `buyer_settlement_remark` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '需方结算备注',
  `settlement_mode` int(11) DEFAULT NULL COMMENT '结算方式 1-信用证 2-代购 3-信用证例外',
  `settlement_amount` double DEFAULT NULL COMMENT '结算金额（元）',
  `status` int(11) DEFAULT '1' COMMENT '状态',
  `create_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '创建人',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_by` varchar(30) COLLATE utf8_bin DEFAULT NULL COMMENT '修改人',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='结算信息表';




DROP TABLE IF EXISTS `t_sys_permission`;

CREATE TABLE `t_sys_permission` (
  `id` varchar(32) COLLATE utf8_bin DEFAULT NULL,
  `user_id` varchar(32) COLLATE utf8_bin DEFAULT NULL,
  `permissions` varchar(5000) COLLATE utf8_bin DEFAULT NULL,
  `permission_type` int(11) DEFAULT NULL,
  `status` int(11) DEFAULT NULL,
  `create_by` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
